import React, { Component } from 'react'

class Calc extends Component {
    constructor(){
        super()
        this.state={
            output:"Output will be displayed here"
        }
    }
    clear(){
        this.setState({
            output:""
        })
    }
    render() {
    return (
        <div>
            <h1>{this.state.output}</h1>
            <button onClick={()=>this.clear()}>Clear</button>
        </div>
    )
    }
}

export default Calc
